package com.justimagine.com;

public class PrimitiveVarible {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b=10;
		short s=23000;
		int a=230;
		long l=1422133424;
		char c='C';
		float f=1.0f;
		double d=102.55d;
		boolean bl=true;
		
		System.out.println("This is Byte data type:"+a);
		System.out.println("This is int data type:"+b);
		System.out.println("This is char data type:"+c);
		System.out.println("This is float data type:"+f);
		System.out.println("This is double data type:"+d);
		System.out.println("This is short data type:"+s);
		System.out.println("This is long data type:"+l);
		System.out.println("This is boolean data type:"+bl);
	}

}
