package com.justimgine.controlstatement;

public class SwappingValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub50
		
		
		int a=50;
		int b=30;
		int temp=0;
		
		System.out.println("Value of a before Swapping :"+a+"Value of b before swapping:"+b);
		temp=a;
		a=b;
		b=temp;
		System.out.println("Value of  a after Swapping:"+a+"Value of b after swapping:"+b);
		
		

	}

}
